
import math
import pprint
import decimal
import fractions

numerator = ("47449023823422717451594836169381198840134089164995011450245185" +
             "61838777684717684085417406717376659694422247951574014327387976" +
             "8480709913455315809719544718248")
denominator = ("151034933727656572741457537643244123466736157365649904806452" +
               "295026919400951667087834668197436704093923364340930317605883" +
               "86010342390699617474201564040715683")

fraction_pi = fractions.Fraction (numerator + "/" + denominator)

decimal_pi_text = "3."

decimal_pi = decimal.Decimal(decimal_pi_text)

print ("pretty print of fraction_pi")
pprint.pprint (fraction_pi)

print ("fraction_pi with decimal formatting")
fraction_pi_text = "{:.311e}".format(fraction_pi)
print (fraction_pi_text)
       
print ("float(fraction_pi)")
pprint.pprint(float(fraction_pi))

print ("Math.pi as a fraction")
fraction_math_pi = fractions.Fraction (math.pi)
pprint.pprint(fraction_math_pi)

print ("fraction_pi - math.pi as a fraction")
pprint.pprint (fraction_pi - fraction_math_pi)

print ("fraction_pi - math.pi as a float")
pprint.pprint (float (fraction_pi - fraction_math_pi))

print ("decimal pi")
pprint.pprint (decimal_pi)

print ("decimal pi as a fraction")
pprint.pprint (fractions.Fraction(decimal_pi))

print ("decimal pi - fraction_pi as a fraction")
pprint.pprint (fractions.Fraction(decimal_pi) - fraction_pi)

print ("decimal pi - fraction pi as a decimal")
pprint.pprint (decimal_pi - decimal.Decimal(fraction_pi_text))

